import axios from 'axios';

// Pode ser algum servidor executando localmente:
// http://localhost:3000

const api = axios.create({
  baseURL: 'http://localhost:5000',
});

async function getEnderecoPorCep() {
  try {
    await api.get('https://viacep.com.br/ws/60352590/json/');    
  } catch (error) {
    console.error(error);
  }
}

export default api;
