import {
  ADICIONAR_ITEM_CARRINHO,
  REMOVER_ITEM_CARRINHO,
} from '../constantes/constantesCarrinhoCompras.js';
import { SALVAR_ENDERECO_ENVIO } from '../constantes/constantesEnderecoEnvio.js';

export const enderecoEnvioReducer = (state = { endereco: {} }, action) => {
  switch (action.type) {
    case SALVAR_ENDERECO_ENVIO:
      return { endereco: action.payload };
    default:
      return state;
  }
};
